definition.script:
pipeline {
    agent {
        label 'controller-j'
    }
    parameters {
        string defaultValue: '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', description: 'github token', name: 'token', trim: false
        string defaultValue: 'github.com/intel-innersource/applications.validation.network-platform.npxtester.git', description: 'github repo url', name: 'git_repo_url', trim: false
        string defaultValue: '10.239.182.209', description: '测试机IP', name: 'sut1_ip', trim: false
        string defaultValue: 'rehat', description: '测试机器系统:rehat or ubuntu', name: 'os', trim: false
        string defaultValue: 'ens785f2', description: '测试机网口名称', name: 'pf1', trim: false
        string defaultValue: '10.239.182.79', description: '发报机IP', name: 'sut2_ip', trim: false
        string defaultValue: 'ens787f2', description: '发报机网口名称', name: 'sut2_pf1', trim: false
        string defaultValue: '25', description: '网卡带宽', name: 'pf1_bandwidth', trim: false
        string defaultValue: '600', description: '运行时间', name: 'run_time', trim: false
        string defaultValue: '1.6.4', description: 'driver version', name: 'drive_version', trim: false
    }
    stages{
        stage('rm project dir'){
            steps{
                script{
                    out=sh(script:"ls $WORKSPACE/npxtester",returnStatus:true)
                    if(out == 0){
                        println "dir is exists and rm dir"
                        sh "rm -rf $WORKSPACE/npxtester"
                    }
                }
            }
        }
        stage('clone'){
            steps{
                sh "git clone -b develop https://$token@$git_repo_url npxtester"
            }
        }
        stage('set args'){
            steps{
                sh "python $WORKSPACE/npxtester/conf/change_args.py  '{\"drive_info\":{\"drive_version\":\"$drive_version\"},\"sut_conf\":{\"host\":\"$sut1_ip\",\"run_time\":\"$run_time\",\"pf1\":\"$pf1\",\"os\":\"$os\",\"pf1_bandwidth\":\"$pf1_bandwidth\"},\"pkg_generator\":{\"host\":\"$sut2_ip\",\"pf1\":\"$sut2_pf1\",\"pf1_bandwidth\":\"$pf1_bandwidth\"}}'"
            }
        }
        stage('run nic case'){
            steps{
                sh "pytest $WORKSPACE/npxtester/Tests/testNics -s -m iperf_tcp --capture=tee-sys --html=/root/testconfig/testlogs/HtmlReport/nic_iperf_4hours_tcp_report.html --self-contained-html"
            }
        }
        stage('collect logs'){
            steps{
                sh "sed -i 's/\r//g' $WORKSPACE/npxtester/log_analyzer/log_analyzer.sh"
                sh "chmod a+x $WORKSPACE/npxtester/log_analyzer/log_analyzer.sh"
                sh "sh $WORKSPACE/npxtester/log_analyzer/log_analyzer.sh"
            }
        }
    }
    post {
        always {
            emailext attachLog: true, body: "${currentBuild.currentResult}: Job ${env.JOB_NAME} build ${env.BUILD_NUMBER}\n More info at: ${env.BUILD_URL}", recipientProviders: [[$class: 'DevelopersRecipientProvider'], [$class: 'RequesterRecipientProvider']], subject: "Jenkins Build ${currentBuild.currentResult}: Job ${env.JOB_NAME}"
        }
    }
}
