definition.script:
pipeline {
    agent {
        label 'auto-controller'
    }
    parameters {
        string defaultValue: '10.112.97.157', description: '测试机IP', name: 'sut1_ip', trim: false
	    string defaultValue: '192.168.10.10', description: 'DHCP ipv4', name: 'dhcp_ipv4', trim: false
	    string defaultValue: 'fe80::b696:91ff:fe9c:f762', description: 'DHCP ipv6', name: 'dhcp_ipv6', trim: false
	    string defaultValue: 'rehat', description: '测试机器系统:rehat or ubuntu', name: 'os', trim: false
	    string defaultValue: 'ens787f0', description: '链接DHCP网口名称', name: 'pf1', trim: false
    }
    stages{
        stage('set args'){
            steps{
                sh "python $WORKSPACE/npxtester/conf/change_args.py  '{\"sut_conf\":{\"host\":\"$sut1_ip\",\"connect_switch_sut_pf\":\"$pf1\",\"pf1\":\"$pf1\",\"os\":\"$os\"},\"dhcp\":{\"ipv4\":\"$dhcp_ipv4\",\"ipv6\":\"$dhcp_ipv6\"}}'"
            }
        }
        stage('run nic case'){
            steps{
                sh "pytest $WORKSPACE/npxtester/Tests/testNics -s -m nicSimple --html=/root/testconfig/testlogs/HtmlReport/nic_simple_case_report.html --self-contained-html"
            }
        }
    }
}
